</template>

<script>
import { ApiV1 } from '@/Api'

export default {
  name: 'EuropeMap',
  data() {
    return {
      tooltipVisible: false,
      tooltipContent: '',
      tooltipStyle: {
        left: '0px',
        top: '0px'
      },
      citiesInSystem: [],
      showCityPopup: false,
      selectedCountry: '',
      filteredCities: [],
      loggedInStatus: !!localStorage.getItem('x-auth-token'),
      showLoginMessage: false,
      isAdmin: false,
      showNewCityForm: false,
      isEditing: false,
      editCityId: null,
      newCityName: '',
      newCityCountry: '',
      goodToKnow: '',
      stats: '',
      facts: '',
      rating: null,
      tagOptions: [
        'historical',
        'quiet',
        'party',
        'architecture',
        'recently added',
        'nature',
        'beachy',
        'warm weather',
        'cold weather',
        'popular',
        'affordable',
        'high-end',
        'lgbtq+ friendly',
        'walkable',
        'small city',
        'big city'
      ],
      selectedTags: []
    }
  },
  async created() {
    await this.fetchCitiesInSystem()
    await this.checkIfAdmin()
  },
  computed: {
    isLoggedIn() {
      return this.loggedInStatus
    }
  },
  methods: {
    async fetchCitiesInSystem() {
      try {
        const response = await ApiV1.get('/api/cities')
        if (response.data && response.data.cities) {
          this.citiesInSystem = response.data.cities
        } else {
          console.error('No cities found')
        }
      } catch (error) {
        console.error('Error fetching cities:', error)
      }
    },
    async loadCities() {
      try {
        const response = await ApiV1.get('/api/cities')
        this.citiesInSystem = response.data
      } catch (error) {
        console.error('Error loading cities:', error)
      }
    },
    isClickable(country) {
      const hasCities = this.citiesInSystem.some(city => city.country === country)
      return hasCities
    },
    async checkIfAdmin() {
      try {
        const response = await ApiV1.get('/api/admin/verify-admin', {
          headers: {
            'x-auth-token': localStorage.getItem('x-auth-token')
          }
        })
        this.isAdmin = response.data.isAdmin
      } catch (error) {
        console.error('Error checking admin status:', error)
      }
    },
    countryClicked(country) {
      if (!this.isLoggedIn) {
        this.showLoginMessage = true
        return
      }

      this.selectedCountry = country
      this.filteredCities = this.citiesInSystem.filter(city => city.country === country)

      if (!this.isAdmin && this.filteredCities.length === 0) {
        return
      }

      if (this.filteredCities.length === 0 && this.isAdmin) {
        this.filteredCities = [{ cityName: 'No cities for this country yet', _id: 'no-cities' }]
      }
      this.showCityPopup = true
    },
    showTooltip(event, country) {
      this.tooltipContent = country
      this.tooltipVisible = true
      this.tooltipStyle = {
        left: `${event.pageX + 10}px`,
        top: `${event.pageY - 30}px`
      }
    },
    hideTooltip() {
      this.tooltipVisible = false
    },
    isInSystem(country) {
      if (this.isAdmin) {
        return true
      }
      return this.citiesInSystem.some(city => city.country === country)
    },
    closeCityPopup() {
      this.showCityPopup = false
    },
    hideLoginMessage() {
      this.showLoginMessage = false
    },
    async editCity(cityId) {
      this.showCityPopup = false
      this.isEditing = true

      try {
        const response = await ApiV1.get(`/api/cities/${cityId}`, {
          headers: {
            'x-auth-token': localStorage.getItem('x-auth-token')
          }
        })

        if (response.data && response.data.city) {
          const cityData = response.data.city

          this.newCityName = cityData.cityName
          this.newCityCountry = cityData.country
          this.goodToKnow = cityData.goodToKnow
          this.stats = cityData.statistics
          this.facts = cityData.facts
          this.rating = cityData.rating
          this.selectedTags = cityData.tags || []

          this.editCityId = cityId
          this.showNewCityForm = TextTrackCueList
        }
      } catch (error) {
        console.error('Error fetching city data:', error)
        alert('Failed to load city data for editing.')
      }

      await this.fetchCitiesInSystem()
    },
    async deleteCity(cityId) {
      const confirmed = confirm('Are you sure you want to delete this city and all its associated places?')
      if (!confirmed) {
        return
      }

      if (!cityId) {
        console.error('City ID is undefined')
        return
      }

      try {
        // Fetch the city details to get places to visit
        const cityResponse = await ApiV1.get(`/api/cities/${cityId}`, {
          headers: { 'x-auth-token': localStorage.getItem('x-auth-token') }
        })

        if (cityResponse.status !== 200) {
          console.error('Failed to fetch city details:', cityResponse.data.message)
          return
        }

        const city = cityResponse.data.city // Assuming the city object is in data.city
        const placesToVisit = city.placesToVisit || [] // Places should already be populated with full place details
        console.log('City:', city)
        console.log('Places to Visit:', placesToVisit)

        // Iterate over the places and delete each one using its address
        for (const place of placesToVisit) {
          const placeAddress = place.address
          console.log(placeAddress)

          if (placeAddress) {
            try {
              const deleteResponse = await ApiV1.delete(`/api/places/${placeAddress}`, {
                headers: { 'x-auth-token': localStorage.getItem('x-auth-token') }
              })

              if (deleteResponse.status === 200) {
                console.log(`Deleted place at address ${placeAddress} successfully.`)
              } else {
                console.error(`Failed to delete place at address ${placeAddress}:`, deleteResponse.data.message)
              }
            } catch (error) {
              console.error(`Error deleting place at address ${placeAddress}:`, error)
            }
          } else {
            console.error('Place does not have a valid address:', place)
          }
        }

        // Now delete the city itself
        const deleteCityResponse = await ApiV1.delete(`/api/admin/cities/${cityId}`, {
          headers: { 'x-auth-token': localStorage.getItem('x-auth-token') }
        })

        if (deleteCityResponse.status === 200) {
          this.filteredCities = this.filteredCities.filter(city => city._id !== cityId)
          alert('City and all associated places deleted successfully.')
        } else {
          console.error('Failed to delete city:', deleteCityResponse.data.message)
        }
      } catch (error) {
        console.error('Error deleting city:', error)
      }

      // Fetch the updated list of cities
      await this.fetchCitiesInSystem()
    },
    createNewCity() {
      this.showCityPopup = false
      this.newCityCountry = this.selectedCountry
      this.showNewCityForm = true
      this.newCityName = ''
      this.goodToKnow = ''
      this.stats = ''
      this.facts = ''
      this.rating = null
      this.selectedTags = []
    },
    async submitNewCity() {
      console.log('create new city')
      if (this.rating > 5) {
        alert('Rating cannot be more than 5. Please enter a valid rating.')
        return
      }
      const cityData = this.prepareCityData()

      try {
        const response = await ApiV1.post('/api/cities', cityData, {
          headers: {
            'x-auth-token': localStorage.getItem('x-auth-token')
          }
        })

        if (response.status === 201) {
          alert('City successfully created!')
          await this.fetchCitiesInSystem()
          this.closeNewCityForm()
        }
      } catch (error) {
        console.error('Error saving city:', error)
        alert('Failed to save city: ' + (error.response?.data?.message || error.message))
      }
    },
    async submitEditCity() {
      console.log('edit')
      if (this.rating > 5) {
        alert('Rating cannot be more than 5. Please enter a valid rating.')
        return
      }
      const cityData = this.prepareCityData()

      try {
        const response = await ApiV1.put(`/api/cities/${this.editCityId}`, cityData, {
          headers: {
            'x-auth-token': localStorage.getItem('x-auth-token')
          }
        })

        if (response.status === 200) {
          alert('City successfully updated!')
          await this.fetchCitiesInSystem()
          this.closeNewCityForm()
        }
      } catch (error) {
        console.error('Error updating city:', error)
        alert('Failed to update city: ' + (error.response?.data?.message || error.message))
      }
    },
    prepareCityData() {
      return {
        cityName: this.newCityName,
        country: this.newCityCountry,
        goodToKnow: this.goodToKnow,
        statistics: this.stats,
        facts: this.facts,
        rating: this.rating,
        tags: this.selectedTags
      }
    },
    resetForm() {
      this.newCityName = ''
      this.goodToKnow = ''
      this.stats = ''
      this.facts = ''
      this.rating = null
      this.selectedTags = []
      this.isEditing = false
      this.editCityId = null
    },
    closeNewCityForm() {
      console.log('closeNewCityForm called')
      this.showNewCityForm = false
      this.resetForm()
    }
  }
}
</script>

