            @mouseleave="hideTooltip"
            @click="countryClicked('Portugal')"
            ></path>

          </svg>

<!-- Tooltip for country names -->
<div v-if="tooltipVisible" :style="tooltipStyle" class="tooltip">{{ tooltipContent }}</div>

<!-- Overlay for non-logged-in users -->
<div class="overlay" v-if="!isLoggedIn && showLoginMessage" @click="hideLoginMessage"></div>

<!-- City Popup Window -->
<div class="city-popup" v-if="isLoggedIn && showCityPopup">
  <div class="popup-header">
    <h3>Cities in {{ selectedCountry }}</h3>
    <button @click="closeCityPopup">X</button>
  </div>
  <div class="popup-body">
    <ul>
      <li v-for="city in filteredCities" :key="city._id || city.cityName">
        {{ city.cityName }}
        <button v-if="isAdmin && city._id !== 'no-cities'" @click="editCity(city._id)">Edit</button>
        <button v-if="isAdmin && city._id !== 'no-cities'" @click="deleteCity(city._id)">Delete</button>
      </li>
    </ul>
  </div>
  <!-- New Button for Admins to Create a New City -->
  <div v-if="isAdmin" class="popup-footer">
    <button @click="createNewCity">Create New City</button>
  </div>
</div>

<!-- Overlay for the New City Form -->
<div class="overlay" v-if="showNewCityForm" @click="closeNewCityForm"></div>

<!-- New City Form Popup -->
<!-- New City Form Popup -->
<div class="new-city-popup" :class="{ show: showNewCityForm }" v-if="isLoggedIn && showNewCityForm">
  <div class="popup-header">
    <h3>Create New City</h3>
    <button @click="closeNewCityForm">X</button>
  </div>
  <div class="popup-body">
    <form @submit.prevent="submitNewCity">
      <div class="form-layout">
        <div class="form-left">
          <label for="cityName">City Name:</label>
          <input type="text" id="cityName" v-model="newCityName" required />

          <label for="country">Country:</label>
          <input type="text" id="country" v-model="newCityCountry" readonly />

          <label for="goodToKnow">Good to Know:</label>
          <textarea id="goodToKnow" v-model="goodToKnow" placeholder="Enter important information..."></textarea>

          <label for="stats">Stats:</label>
          <input type="text" id="stats" v-model="stats" placeholder="Enter stats about the city..." />

          <label for="facts">Facts:</label>
          <textarea id="facts" v-model="facts" placeholder="Enter interesting facts about the city..."></textarea>

          <label for="rating">Rating:</label>
          <input type="number" id="rating" v-model="rating" min="0" max="10" step="0.1" placeholder="Enter a rating (0-5)" />
        </div>
        <div class="form-right">
          <label for="tags">Tags:</label>
          <div class="tags">
            <label v-for="tag in tagOptions" :key="tag">
              <input type="checkbox" :value="tag" v-model="selectedTags" />
              {{ tag }}
            </label>
          </div>
        </div>
      </div>
      <button type="submit">Submit</button>
    </form>
  </div>
</div>

<!-- Message for non-logged-in users -->
<div v-if="!isLoggedIn && showLoginMessage" class="login-message">
