  margin: 0;
  padding: 0;
}

svg path {
  fill: #8FC6DF; /* Color of the countries */
  stroke: #BC672A; /* Border between countries */
  stroke-width: 0.25;
}

.system-country:hover {
  fill: #BC672A;
  transition: 0.6s;
  cursor: pointer; /* Countries in the system have pointer cursor */
}

.not-in-system:hover {
  fill: #8FC6DF; /* Optional: Keep the color unchanged */
  cursor: default; /* Countries not in the system have default cursor */
}

